import Matter from 'matter-js';

export const setupWorld = (engine, players) => {
  const world = engine.world;

  // 공 생성
  players.forEach((player, index) => {
    const ball = Matter.Bodies.circle(50 + index * 50, 100, 20, {
      label: player,
      restitution: 0.8,
    });
    Matter.World.add(world, ball);
  });

  // 장애물 추가 예제
  const obstacle = Matter.Bodies.rectangle(400, 300, 100, 20, { isStatic: true });
  Matter.World.add(world, obstacle);

  return world;
};
