import React, { useEffect, useRef } from 'react';
import Matter from 'matter-js';
import { setupWorld } from '../physics/physicsEngine';
import { setupCollisionEvents } from '../physics/collision';

const GameCanvas = ({ players }) => {
  const canvasRef = useRef(null);
  const engine = useRef(Matter.Engine.create());
  const world = engine.current.world;

  // Î†åÎçîÎßÅÌï† Îïå console.logÎ°ú ÏÉÅÌÉú ÌôïÏù∏
  console.log('Players:', players);

  useEffect(() => {
    if (!canvasRef.current) return;

    // Ï∫îÎ≤ÑÏä§ Ï¥àÍ∏∞Ìôî
    const render = Matter.Render.create({
      element: canvasRef.current,
      engine: engine.current,
      options: {
        width: 800,
        height: 600,
        wireframes: false,
        background: '#f0f0f0',
      },
    });

    // Ï§ëÎ†• ÏÑ§Ï†ï
    engine.current.gravity.y = 1; // Ï§ëÎ†•ÏùÑ ÏïÑÎûòÎ°ú ÏÑ§Ï†ï

    // ÏóîÏßÑ Ïã§Ìñâ
    Matter.Engine.run(engine.current);
    Matter.Render.run(render);

    // Ïª¥Ìè¨ÎÑåÌä∏ Ïñ∏ÎßàÏö¥Ìä∏ Ïãú Matter ÏóîÏßÑ Î∞è Î†åÎçîÎßÅ Ï§ëÏßÄ
    return () => {
      Matter.Engine.clear(engine.current);
      Matter.Render.stop(render);
    };
  }, []); // ÏùòÏ°¥ÏÑ± Î∞∞Ïó¥Ïóê Îπà Î∞∞Ïó¥Ïù¥ÎØÄÎ°ú Ìïú Î≤àÎßå Ïã§Ìñâ

  useEffect(() => {
    if (players.length > 0) {
      // ÏõîÎìú Ï¥àÍ∏∞Ìôî
      const world = engine.current.world;
      Matter.World.clear(world);
      setupWorld(engine.current, players);

      // Ï∂©Îèå Ïù¥Î≤§Ìä∏ ÏÑ§Ï†ï
      setupCollisionEvents(engine.current, (winner) => {
        console.log(`üéâ Ïö∞ÏäπÏûê: ${winner}`);
        alert(`Ïö∞ÏäπÏûêÎäî ${winner}ÏûÖÎãàÎã§!`);
      });
    }
  }, [players]); // players ÏÉÅÌÉúÍ∞Ä ÏóÖÎç∞Ïù¥Ìä∏Îê† ÎïåÎßàÎã§ ÏóÖÎç∞Ïù¥Ìä∏

  return (
    <div style={{ flex: 1 }}>
      {/* canvasRefÎ°ú ÏÑ§Ï†ïÌïú Ï∫îÎ≤ÑÏä§Î•º Î†åÎçîÎßÅ */}
      <canvas ref={canvasRef} width="800" height="600" style={{ background: '#f0f0f0' }} />
    </div>
  );
};

export default GameCanvas;
