// 수정된 `setupWorld.js` 코드

import Matter from 'matter-js';

export const setupWorld = (engine, players) => {
  const world = engine.world;

  // 바닥 추가
  const ground = Matter.Bodies.rectangle(400, 590, 800, 20, {
    isStatic: true,
    render: { fillStyle: '#000' },
  });
  Matter.World.add(world, ground);

  // 공을 생성
  players.forEach((player, index) => {
    const ball = Matter.Bodies.circle(100 + index * 50, 50, 20, {
      label: player,
      restitution: 0.8, // 튀기는 정도
      render: { fillStyle: randomColor() },  // 랜덤 색상 설정
    });
    Matter.World.add(world, ball);
  });

  // 장애물 예제
  const obstacles = [
    Matter.Bodies.rectangle(400, 300, 150, 20, { isStatic: true, angle: Math.PI / 4 }),
    Matter.Bodies.rectangle(200, 400, 100, 20, { isStatic: true }),
  ];
  Matter.World.add(world, obstacles);

  return world;
};

const randomColor = () => {
  const letters = '0123456789ABCDEF';
  let color = '#';
  for (let i = 0; i < 6; i++) {
    color += letters[Math.floor(Math.random() * 16)];
  }
  return color;
};
