import React, { useState } from "react";

const UserPanel = ({ onStartGame, onShufflePlayers, onUpdateSettings }) => {
  const [map, setMap] = useState("default"); // 선택된 맵
  const [players, setPlayers] = useState([]); // 참여자 이름 목록

  const addPlayer = () => {
    const newPlayer = prompt("참여자 이름을 입력하세요:");
    if (newPlayer) setPlayers([...players, newPlayer]);
  };

  return (
    <div className="user-panel">
      <h2>게임 설정</h2>
      <label>
        맵 선택:
        <select value={map} onChange={(e) => setMap(e.target.value)}>
          <option value="default">기본 맵</option>
          <option value="obstacle-heavy">장애물 많은 맵</option>
        </select>
      </label>
      <button onClick={addPlayer}>참여자 추가</button>
      <ul>
        {players.map((player, index) => (
          <li key={index}>{player}</li>
        ))}
      </ul>
      <button onClick={onShufflePlayers}>섞기</button>
      <button onClick={() => onStartGame(players, map)}>시작</button>
    </div>
  );
};

export default UserPanel;
