// ìˆ˜ì •ëœ `GameCanvas.js`

import React, { useEffect, useRef } from 'react';
import Matter from 'matter-js';
import { setupWorld } from '../physics/physicsEngine';
import { setupCollisionEvents } from '../physics/collision';

const GameCanvas = ({ players }) => {
  const canvasRef = useRef(null);
  const engineRef = useRef(Matter.Engine.create());
  const worldRef = useRef(engineRef.current.world);

  useEffect(() => {
    const render = Matter.Render.create({
      element: canvasRef.current,
      engine: engineRef.current,
      options: {
        width: 800,
        height: 600,
        wireframes: false,
        background: '#f0f0f0',
      },
    });

    Matter.Engine.run(engineRef.current);
    Matter.Render.run(render);

    return () => {
      Matter.Render.stop(render);
      Matter.Engine.clear(engineRef.current);
    };
  }, []);

  useEffect(() => {
    // ì›”ë“œ ì´ˆê¸°í™”
    Matter.World.clear(worldRef.current);
    setupWorld(engineRef.current, players);
    setupCollisionEvents(engineRef.current, (winner) => {
      console.log(`ðŸŽ‰ ìš°ìŠ¹ìž: ${winner}`);
      alert(`ìš°ìŠ¹ìžëŠ” ${winner}ìž…ë‹ˆë‹¤!`);
    });
  }, [players]);

  const startGame = () => {
    Matter.Engine.run(engineRef.current);
    Matter.Render.run(render);
  };

  return <div ref={canvasRef}></div>;
};

export default GameCanvas;
