import React, { useState } from 'react';

const UserPanel = ({ onStart, onShuffle, addPlayer }) => {
  const [playerName, setPlayerName] = useState('');

  const handleAddPlayer = () => {
    if (playerName.trim()) {
      addPlayer(playerName);
      setPlayerName('');
    }
  };

  return (
    <div style={{ padding: '20px', width: '300px', borderRight: '1px solid #ccc' }}>
      <h3>BetBall 설정</h3>
      <input
        type="text"
        placeholder="참여자 이름"
        value={playerName}
        onChange={(e) => setPlayerName(e.target.value)}
      />
      <button onClick={handleAddPlayer}>참여자 추가</button>
      <br />
      <button onClick={onShuffle}>섞기</button>
      <button onClick={onStart}>시작</button>
    </div>
  );
};

export default UserPanel;
