import React, { useEffect, useRef } from 'react';
import Matter from 'matter-js';
import { setupWorld } from '../physics/physicsEngine';
import { setupCollisionEvents } from '../physics/collision';

const GameCanvas = ({ players }) => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const engine = Matter.Engine.create();
    const render = Matter.Render.create({
      element: canvasRef.current,
      engine: engine,
      options: {
        width: 800,
        height: 600,
        wireframes: false,
        background: '#f0f0f0',
      },
    });

    // ÏõîÎìú Ï¥àÍ∏∞Ìôî
    const world = setupWorld(engine, players);

    // Ï∂©Îèå Ïù¥Î≤§Ìä∏ Ï≤òÎ¶¨
    setupCollisionEvents(engine, (winner) => {
      console.log(`üéâ Ïö∞ÏäπÏûê: ${winner}`);
      alert(`Ïö∞ÏäπÏûêÎäî ${winner}ÏûÖÎãàÎã§!`);
    });

    // Matter.js ÏóîÏßÑÍ≥º Î†åÎçîÎü¨ Ïã§Ìñâ
    Matter.Engine.run(engine);
    Matter.Render.run(render);

    return () => {
      // ÌÅ¥Î¶∞ÏóÖ
      Matter.Render.stop(render);
      Matter.Engine.clear(engine);
    };
  }, [players]);

  return <div ref={canvasRef}></div>;
};

export default GameCanvas;
